import React, {useEffect, useRef, useState}from 'react';
import '../styles/tour-details.css';
import { Container, Row, Col, Form, ListGroup } from 'reactstrap';
import { useParams } from 'react-router-dom';
import calculateAvgRating from '../utils/avgRating';
import avatar from '../assets/images/avatar.jpg';
import Booking from '../components/Booking/Booking';
import Newsletter from './../shared/Newsletter';
import useFetch from './../hooks/useFetch';
import { BASE_URL } from './../utils/config';

const TourDetails = () => {
  const { id } = useParams();
  const reviewMsgRef = useRef('')
  const {tourRating, setTourRating}= useState(null)

  // fetch data from database
  const {data:response, loading, error } = useFetch(`${BASE_URL}/tours/${id}`);
  const tour = response?.data[0] || [];

  const { photo, title, desc, fasilitas, price, address, reviews, city, distance, maxGroupSize } = tour;

  const { totalRating, avgRating } = calculateAvgRating(reviews);
 

  //FORMAT DATE

  const options ={day:'numeric', month:'long', year:'numeric'};
   
  // submit request to the server
    const submitHandler = e => {
      e.preventDefault()
      const reviewText = reviewMsgRef.current.value 
  
      // later 
    }

    useEffect(()=>{
      window.scrollTo(0,0)
    },[])

  return (
    <>
      <section>
        <Container>
          {loading && <h4 className="text-center">Loading. . . . . .</h4> }
          { error && <h4 className="text-center">{error}</h4>}
          {!loading && !error &&         
        <Row>
          <Col lg="8">
            <div className="tour__content">
              <img src={photo} alt={title} />

              <div className="tour__info">
                <h1>{title}</h1>

                <div className="d-flex align-items-center gap-5">
                  <span className="tour__rating d-flex align-items-center gap-1">
                    <i className="ri-star-fill" style={{ color: "var(--secondary-color)" }}></i>
                    {avgRating === 0 ? null : avgRating}
                    {totalRating === 0 ? ("Not rated") : (<span>({reviews.length})</span>)}
                  </span>

                  <span>
                    <i className="ri-map-pin-user-fill"></i> {address}
                  </span>
                </div>

                <div className="tour__extra-details">
                  <span>
                    <i className="ri-map-pin-2-line"></i> {city}
                  </span>
                  <span>
                    <i className="ri-wallet-3-line"></i> {price} /pax
                  </span>
                  <span>
                    <i className="ri-group-line"></i> {maxGroupSize}
                  </span>
                </div>
                <h5>Destinasi dan Fasilitas</h5>
                <p>{desc}</p>
                <p>{fasilitas}</p>
              </div>

              {/*============ tour reviews section ============ */}
              <div className="tour__reviews mt-4">
                <h4>Reviews ({reviews?.length} reviews)</h4>

                <Form onSubmit={submitHandler}>
                  <div className='d-flex align-items-center gap-3 mb-4 rating__group'>
                    <span onClick={()=> setTourRating(1)}>
                      1 <i className="ri-star-fill"></i></span>
                    <span onClick={()=> setTourRating(2)}>
                      2 <i className="ri-star-fill"></i></span>
                    <span onClick={()=> setTourRating(3)}>
                      3 <i className="ri-star-fill"></i></span>
                    <span onClick={()=> setTourRating(4)}>
                      4 <i className="ri-star-fill"></i></span>
                    <span onClick={()=> setTourRating(5)}>
                      5 <i className="ri-star-fill"></i></span>
                  </div>

                  <div className="review__input-container">
                    <input className="review__input" type="text" ref={reviewMsgRef} placeholder="Tulis pengalamanmu bersama Voyago" 
                    required />
                    <div className="btn-container">
                      <button className="btn primary__btn text-white" type="submit">
                        Submit
                      </button>
                    </div>
                  </div>
                </Form>

                <ListGroup className="user__reviews">
                  {reviews?.map(review => (
                    <div className="review__item">
                      <img src={avatar} alt="" />

                      <div className="w-100">
                        <div className="d-flex align-items-center justify-content-between">
                          <div>
                            <h5>Radika</h5>
                            <p>{new Date('01-20-2023').toLocaleDateString("en-US",options)}</p>
                          </div>
                          <span className='d-flex align-items-center'>
                          5 <i className="ri-star-fill"></i>
                          </span>
                        </div>

                        <h6>
                          Pengalaman yang seru liburan bersama VoyaGo!
                        </h6>
                      </div>
                    </div>
                  ))}
                </ListGroup>
              </div>
              {/*============ tour reviews section ============ */}
            </div>
          </Col>

          <Col lg='4'>
            <Booking tour = {tour} avgRating = {avgRating}/>

          </Col>
        </Row>
        }
        </Container>
      </section>
      <Newsletter />
    </>
  );
};

export default TourDetails;
