import React, { useState } from 'react';
import './Booking.css';
import { Form, FormGroup, ListGroup, ListGroupItem, Button } from 'reactstrap';

const Booking = ({ tour, avgRating }) => {
    const { price, reviews } = tour;

    const [credentials, setCredentials] = useState({
        userId: '01', // later it will be dynamic
        userEmail: '@gmail.com',
        fullName: '',
        phone: '',
        guestSize: 1,
        bookAt: ''
    });

    const handleChange = e => {
        setCredentials(prev => ({ ...prev, [e.target.id]: e.target.value }));
    };

    // send data to the server
    const handleClick = e => {
        e.preventDefault();
        console.log(credentials);
    };

    return (
        <div className="booking">
            <div className="booking__top d-flex align-items-center justify-content-between">
                <h3>Rp. {price}.000<span> /orang </span></h3>
                <span className="tour__rating d-flex align-items-center">
                    <i className="ri-star-fill"></i>
                    {avgRating === 0 ? null : avgRating} ({reviews?.length})
                </span>
            </div>

            {/* ==================== booking form ================ */}
            <div className="booking__form">
                <h5>Informasi</h5>
                <Form className="booking__info-form" onSubmit={handleClick}>
                    <FormGroup>
                        <input 
                            type="text" 
                            placeholder="Nama Lengkap" 
                            id="fullName"
                            required 
                            onChange={handleChange} 
                        />
                    </FormGroup>
                    <FormGroup>
                        <input 
                            type="number" 
                            placeholder="Nomor telepon" 
                            id="phone"
                            required 
                            onChange={handleChange} 
                        />
                    </FormGroup>
                    <FormGroup className="d-flex align-items-center gap-3">
                        <input 
                            type="date" 
                            placeholder="" 
                            id="bookAt"
                            required 
                            onChange={handleChange} 
                        />
                        <input 
                            type="number" 
                            placeholder="Jumlah orang" 
                            id="guestSize"
                            required 
                            onChange={handleChange} 
                        />
                    </FormGroup>
                </Form>
            </div>
            {/* ==================== booking end ================ */}

            {/* ==================== booking bottom ================ */}
            <div className="booking__bottom">
                <ListGroup>
                    <ListGroupItem className="border-0 px-0">
                        <h5 className="d-flex align-items-center gap-1">
                            Rp. {price}.000 <i className="ri-close-line"></i> 1 orang
                        </h5>
                        <span>Rp. {price}.000</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0">
                        <h5>Tax</h5>
                        <span>Rp. 15.000</span>
                    </ListGroupItem>
                    <ListGroupItem className="border-0 px-0 total">
                        <h5>Total</h5>
                        <span>Rp. 765.000</span>
                    </ListGroupItem>
                </ListGroup>
                <Button className="btn primary__btn w-100 mt-4" onClick={handleClick}>Pesan Sekarang</Button>
            </div>
        </div>
    );
};

export default Booking;
